const styleSwitcherToggle=document.querySelector(".style-switcher-toggler");styleSwitcherToggle.addEventListener("click",()=>{document.querySelector(".style-switcher").classList.toggle("open")}),window.addEventListener("scroll",()=>{document.querySelector(".style-switcher").classList.contains("open")&&document.querySelector(".style-switcher").classList.remove("open")});const alternateStyles=document.querySelectorAll(".alternate-style");function setActiveStyle(e){alternateStyles.forEach(t=>{e===t.getAttribute("title")?t.removeAttribute("disabled"):t.setAttribute("disabled","true")})}const dayNight=document.querySelector(".day-night");dayNight.addEventListener("click",()=>{dayNight.querySelector("i").classList.toggle("fa-moon"),dayNight.querySelector("i").classList.toggle("fa-sun"),document.body.classList.toggle("light")}),window.addEventListener("load",()=>{document.body.classList.contains("light")?dayNight.querySelector("i").classList.add("fa-moon"):dayNight.querySelector("i").classList.add("fa-sun")});